package com.example.data.service.repositoryimport com.example.data.service.api.CatApiimport com.example.data.service.response.toListModelimport com.example.domain.CatRepositoryimport com.example.domain.model.CatModelimport javax.inject.Injectclass CatRepositoryImp @Inject constructor(private val catApi: CatApi) : CatRepository {    override suspend fun getCatInfo(catsLimit: String, skip: String): Result<List<CatModel>> =        try {            val callResponse = catApi.getCats(catsLimit, skip)            Result.success(callResponse.toListModel())        } catch (e: Exception) {            Result.failure(e)        }}