package com.example.data.service.responseimport com.example.domain.model.CatModelimport com.google.gson.annotations.SerializedNamedata class CatResponse(    @SerializedName("id") val id: String)fun CatResponse.toModel() = CatModel("$CAT_URL$id")fun List<CatResponse>.toListModel(): List<CatModel> = this.map { it.toModel() }const val CAT_URL = "https://cataas.com/cat/"